package checkpoint

import (
	"encoding/json"
	"fmt"
)

// Write stores a checkpoint and its associated session data on the orphan branch.
func (s *Store) Write(cp *Checkpoint, sessionData *SessionFiles) error {
	shard := Shard(cp.ID)
	rest := Rest(cp.ID)

	// Hash all the blob objects
	metaJSON, err := json.MarshalIndent(cp.ToMetadata(), "", "  ")
	if err != nil {
		return fmt.Errorf("marshaling metadata: %w", err)
	}

	metaHash, err := s.hashObject(string(metaJSON))
	if err != nil {
		return fmt.Errorf("hashing metadata: %w", err)
	}

	contentHashHash, err := s.hashObject(sessionData.ContentHash)
	if err != nil {
		return fmt.Errorf("hashing content_hash: %w", err)
	}

	contextHash, err := s.hashObject(sessionData.Context)
	if err != nil {
		return fmt.Errorf("hashing context: %w", err)
	}

	fullHash, err := s.hashObject(sessionData.FullJSONL)
	if err != nil {
		return fmt.Errorf("hashing full.jsonl: %w", err)
	}

	sessionMetaJSON, err := json.MarshalIndent(sessionData.Metadata, "", "  ")
	if err != nil {
		return fmt.Errorf("marshaling session metadata: %w", err)
	}
	sessionMetaHash, err := s.hashObject(string(sessionMetaJSON))
	if err != nil {
		return fmt.Errorf("hashing session metadata: %w", err)
	}

	promptHash, err := s.hashObject(sessionData.Prompt)
	if err != nil {
		return fmt.Errorf("hashing prompt: %w", err)
	}

	// Build session subtree (0/)
	sessionTree, err := s.mktree([]treeEntry{
		{mode: "100644", typ: "blob", hash: contentHashHash, name: "content_hash.txt"},
		{mode: "100644", typ: "blob", hash: contextHash, name: "context.md"},
		{mode: "100644", typ: "blob", hash: fullHash, name: "full.jsonl"},
		{mode: "100644", typ: "blob", hash: sessionMetaHash, name: "metadata.json"},
		{mode: "100644", typ: "blob", hash: promptHash, name: "prompt.txt"},
	})
	if err != nil {
		return fmt.Errorf("creating session tree: %w", err)
	}

	// Build checkpoint subtree (<rest>/)
	cpTree, err := s.mktree([]treeEntry{
		{mode: "100644", typ: "blob", hash: metaHash, name: "metadata.json"},
		{mode: "040000", typ: "tree", hash: sessionTree, name: "0"},
	})
	if err != nil {
		return fmt.Errorf("creating checkpoint tree: %w", err)
	}

	// Get current tree of the branch
	currentTree, err := s.getCurrentTree()
	if err != nil {
		return fmt.Errorf("getting current tree: %w", err)
	}

	// Build new root tree incorporating the existing tree + new shard entry
	newRoot, err := s.addToTree(currentTree, shard, rest, cpTree)
	if err != nil {
		return fmt.Errorf("building root tree: %w", err)
	}

	// Create commit
	parentCommit, err := s.git("rev-parse", checkpointBranch)
	if err != nil {
		return fmt.Errorf("getting parent commit: %w", err)
	}

	commitMsg := fmt.Sprintf("checkpoint: %s", cp.ID)
	commitHash, err := s.git("commit-tree", newRoot, "-p", parentCommit, "-m", commitMsg)
	if err != nil {
		return fmt.Errorf("creating commit: %w", err)
	}

	// Update ref
	_, err = s.git("update-ref", "refs/heads/"+checkpointBranch, commitHash)
	if err != nil {
		return fmt.Errorf("updating ref: %w", err)
	}

	return nil
}

package hooks

import (
	"encoding/json"
	"fmt"
	"log/slog"
	"os"
	"path/filepath"
	"time"

	"github.com/jcleira/ai-workflow-core/internal/agent/claude"
	"github.com/jcleira/ai-workflow-core/internal/attribution"
	"github.com/jcleira/ai-workflow-core/internal/checkpoint"
	"github.com/jcleira/ai-workflow-core/internal/config"
	"github.com/jcleira/ai-workflow-core/internal/git"
)

// PostCommit runs post-commit hook logic.
func (r *Runner) PostCommit() error {
	slog.Debug("post-commit hook running")
	return runPostCommit(r.repoRoot, r.cfg)
}

func runPostCommit(repoRoot string, cfg config.Config) error {
	// Read pre-commit state
	stateFile := filepath.Join(repoRoot, config.PartioDir, "state", "pre-commit.json")
	data, err := os.ReadFile(stateFile)
	if err != nil {
		slog.Debug("no pre-commit state found, skipping checkpoint")
		return nil
	}
	// Remove immediately to prevent re-entry (amend triggers post-commit again)
	_ = os.Remove(stateFile)

	var state preCommitState
	if err := json.Unmarshal(data, &state); err != nil {
		return fmt.Errorf("parsing pre-commit state: %w", err)
	}

	if !state.AgentActive {
		slog.Debug("no agent was active, skipping checkpoint")
		return nil
	}

	// Get current commit hash
	commitHash, err := git.CurrentCommit()
	if err != nil {
		return fmt.Errorf("getting current commit: %w", err)
	}

	// Calculate attribution
	attr, err := attribution.Calculate(commitHash, state.AgentActive)
	if err != nil {
		slog.Warn("could not calculate attribution", "error", err)
		attr = &attribution.Result{AgentPercent: 100}
	}

	// Parse agent session data
	detector := claude.New()
	sessionPath, sessionData, err := detector.FindLatestSession(repoRoot)
	if err != nil {
		slog.Warn("could not read agent session", "error", err)
	}

	// Generate checkpoint ID and amend commit with trailers BEFORE writing
	// the checkpoint, so we capture the post-amend commit hash.
	cpID := checkpoint.NewID()

	trailers := map[string]string{
		"Partio-Checkpoint":  cpID,
		"Partio-Attribution": fmt.Sprintf("%d%% agent", attr.AgentPercent),
	}

	if err := git.AmendTrailers(trailers); err != nil {
		slog.Warn("could not add trailers to commit", "error", err)
	}

	// Get the post-amend commit hash (this is the hash that gets pushed)
	commitHash, err = git.CurrentCommit()
	if err != nil {
		return fmt.Errorf("getting post-amend commit: %w", err)
	}

	// Create checkpoint with the post-amend hash
	cp := &checkpoint.Checkpoint{
		ID:          cpID,
		CommitHash:  commitHash,
		Branch:      state.Branch,
		CreatedAt:   time.Now(),
		Agent:       cfg.Agent,
		AgentPct:    attr.AgentPercent,
		ContentHash: commitHash,
	}

	if sessionData != nil {
		cp.SessionID = sessionData.SessionID
	}

	// Prepare session files
	sessionFiles := &checkpoint.SessionFiles{
		ContentHash: commitHash,
		Context:     "",
		FullJSONL:   "",
		Metadata: checkpoint.SessionMetadata{
			Agent: cfg.Agent,
		},
		Prompt: "",
	}

	if sessionData != nil {
		sessionFiles.Context = sessionData.Context
		sessionFiles.Prompt = sessionData.Prompt
		sessionFiles.Metadata.TotalTokens = sessionData.TotalTokens
		sessionFiles.Metadata.Duration = sessionData.Duration.String()
	}

	if sessionPath != "" {
		rawJSONL, err := claude.ReadRawJSONL(sessionPath)
		if err == nil {
			sessionFiles.FullJSONL = string(rawJSONL)
		}
	}

	// Write checkpoint to orphan branch
	store := checkpoint.NewStore(repoRoot)
	if err := store.Write(cp, sessionFiles); err != nil {
		return fmt.Errorf("writing checkpoint: %w", err)
	}

	slog.Debug("checkpoint created", "id", cpID, "agent_pct", attr.AgentPercent)
	return nil
}

package git

// DiffNumstat returns numstat for a specific commit.
func DiffNumstat(commitHash string) (string, error) {
	return execGit("diff", "--numstat", commitHash+"~1", commitHash)
}

