 1→import useSWR from "swr";
 2→import type { CheckpointMetadata, Message } from "@/types/checkpoint";
 3→
 4→const fetcher = async (url: string) => {
 5→  const res = await fetch(url);
 6→  if (!res.ok) throw new Error(`${res.status}`);
 7→  return res.json();
 8→};
 9→
10→export function useCheckpoints(owner: string, repo: string) {
11→  const { data, error, isLoading } = useSWR<CheckpointMetadata[]>(
12→    `/api/github/repos/${owner}/${repo}/checkpoints`,
13→    fetcher
14→  );
15→
16→  return {
17→    checkpoints: data,
18→    isLoading,
19→    isError: !!error,
20→  };
21→}
22→
23→export function useCheckpoint(owner: string, repo: string, id: string) {
24→  const { data, error, isLoading } = useSWR<CheckpointMetadata>(
25→    `/api/github/repos/${owner}/${repo}/checkpoints/${id}`,
26→    fetcher
27→  );
28→
29→  return {
30→    checkpoint: data,
31→    isLoading,
32→    isError: !!error,
33→  };
34→}
35→
36→export function useSession(owner: string, repo: string, id: string) {
37→  const { data, error, isLoading } = useSWR<Message[]>(
38→    `/api/github/repos/${owner}/${repo}/checkpoints/${id}/session`,
39→    fetcher
40→  );
41→
